UPDATE `notification_templates` SET `email_body` = '<p><strong style=\"font-size: 0.875rem; text-align: var(--bs-body-text-align);\">Order Information</strong><br></p><p>Order ID: {{order_id}}<br>Date/Time: {{created_at}}<br>Invoice Id: {{invoice_id}}<br>Amount: {{amount}}</p><p><br></p><p><strong>Customer Information</strong></p><p>Name: {{name}}<br>Email: {{email}}<br>Address: {{address}}<br>City: {{city}}<br>State: {{state}}<br>Postcode: {{zip}}<br>Country: {{country}}<br>Phone Number: {{phone}}</p><p><br></p><p><strong>Order Items</strong></p><p>{{order_items}}</p><p><br></p><p><strong>ISP Information</strong></p><p>IP: {{client_ip}}<br></p><p><br></p><p>\r\n' WHERE `notification_templates`.`act` = 'ORDER_NOTIFICATION';
 